import type { Metadata } from "next";
import { Flowbite } from "flowbite-react";
import { ThemeMusicBox } from "./ThemeMusicBox";

import { twMerge } from "tailwind-merge";
import Link from "next/link";

export const metadata: Metadata = {
  title: " بی اف تیک | موزیک، موزیک ویدیو",
  description: " سایت بی اف تیک | دانلود موزیک و موزیک ویدیو"
};

export default async function RootMusicBoxLayout({
  children
}: {
  children: React.ReactNode;
}) {
  return (
    <>
      <Flowbite theme={{ theme: ThemeMusicBox }}>
        <div
          className={twMerge(
            "bg-gray-50 dark:bg-gray-900 relative min-h-screen flex items-center justify-center"
          )}
        >
          <div className=" w-full sm:w-6/12 lg:w-4/12 xl:w-4/12 border  bg-white min-h-screen z-10 relative">
            <HeaderPart />
            <main className="">{children}</main>
            {/* <FooterPart /> */}
            <FooterPart2 />
          </div>
        </div>
      </Flowbite>
    </>
  );
}

